var spawn = require('./spawn-cmd').spawn;

//
// Module
//
var cmds = module.exports = {};

//
// Download using wget
//
cmds.download = function (opts, next) {
  return spawn('wget', [opts.src, '-P', opts.dest], next);
};

//
// Update apt-get
//
cmds.update = function (next) {
  return spawn('apt-get', ['update'], next);
};

//
// Install using apt-get
//
cmds.install = function (opts, next) {
  return spawn('apt-get', ['install', opts.pkg, '-y'], next);
};